import { createSlice } from "@reduxjs/toolkit";
import produce from "immer";

const initialState = {
    visible: false,
    cartItems : [],
    itemsCounts: 0,
    total: 0
}

export const cartSlice = createSlice({
    name: 'cartReducer',
    initialState,
    reducers: {
        setVisible: (state)=>{
            state.visible = !state.visible;
        }
    }
})

export const { setVisible } = cartSlice.actions;
export default cartSlice.reducer;
