import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { authentication,createUserProfileDocument} from '../../firebase/firebase.utils';
import './sign-up.styles.scss';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { useState} from 'react';

export default function SignUp()
{
    const [displayNaame,setDisplayName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [confirmPassword,setConfirmPassword] = useState('');
    const [user,setUser] = useState({});
    const handleSubmit = async e=> {
        e.preventDefault();
        if(password!==confirmPassword){
            alert('Password does not match');
            return;
        }
        try{

            createUserWithEmailAndPassword(authentication,email,password)
            .then((userCredential) => {
                const {uid,email,phoneNumber,photoURL,displayName} = userCredential.user;
                const name = displayName !== null ? displayName : displayNaame; 
                setUser({
                    uid,email,phoneNumber,photoURL,displayName: name
                })
            });
            console.log(user,'-chari de');
            await createUserProfileDocument(user);


                    // setDisplayName('');
                    // setEmail('');
                    // setConfirmPassword('');
                    // setPassword('');

            //setUser('');

        }catch(e){
            console.log(e);
        }

    }
    return(
        <div className="sign-up">
            <h2 className="title">I do not have a account.</h2>
            <span>Sign Up with Your Email and Password.</span>

           <form  onSubmit={handleSubmit}>
           <FormInput name="displayName" type="text" 
           label="Display Name" required onChange={(e)=>setDisplayName(e.target.value)} value={displayNaame}  />

            <FormInput name="email" type="email" label="Email" onChange={(e)=>setEmail(e.target.value)}  value={email} required />

            <FormInput name="password" type="password" label="Password" onChange={(e)=>setPassword(e.target.value)} value={password} required />

            <FormInput name="confirmPassword" type="password" label="Password" onChange={(e)=>setConfirmPassword(e.target.value)} value={confirmPassword} required/>

            <CustomButton type="submit" value="Submit" >Sign Up</CustomButton>
           </form> 

        </div>
    )
}
