import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { authentication,createUserProfileDocument} from '../../firebase/firebase.utils';
import './sign-up.styles.scss';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { useState} from 'react';

export default function SignUp()
{
    const [displayName,setDisplayName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [confirmPassword,setConfirmPassword] = useState('');
    const handleSubmit = async e => {
        console.log(displayName);
        e.preventDefault();
        if(password!==confirmPassword){
            alert('Password does not match');
            return;
        }
        try{
            const {userCredential} = createUserWithEmailAndPassword(authentication,email,password);
            console.log(userCredential);
            await createUserProfileDocument(userCredential.user,{displayName});
        }catch(e){
            console.log(e);
        }

    }
    return(
        <div className="sign-up">
            <h2 className="title">I do not have a account.</h2>
            <span>Sign Up with Your Email and Password.</span>

           <form  onSubmit={handleSubmit}>
           <FormInput name="displayName" type="text" 
           label="Display Name" required onChange={(e)=>setDisplayName(e.target.value)} value={displayName}  />

            <FormInput name="email" type="email" label="Email" onChange={(e)=>setEmail(e.target.value)}  value={email} required />

            <FormInput name="password" type="password" label="Password" onChange={(e)=>setPassword(e.target.value)} value={password} required />

            <FormInput name="confirmPassword" type="password" label="Password" onChange={(e)=>setConfirmPassword(e.target.value)} value={confirmPassword} required/>

            <CustomButton type="submit" value="Submit" >Sign Up</CustomButton>
           </form> 

        </div>
    )
}
