import React from 'react';
import FormInput from '../form-input/form-input.component';
import CustomButton from '../custom-button/custom-button.component';
import { authentication,createUserProfileDocument } from '../../firebase/firebase.utils';
import './sign-up.styles.scss';
import { createUserWithEmailAndPassword } from 'firebase/auth';
import { useState } from 'react';
export default function SignUp()
{
    const [displayName,setDisplayName] = useState('');
    const [email,setEmail] = useState('');
    const [password,setPassword] = useState('');
    const [confirmPassword,setConfirmPassword] = useState('');
    const handleChange = e => {

    }
    const handleSubmit = async e => {
        e.preventDefault();
        if(password!==setConfirmPassword){
            alert('Password does not match');
            return;
        }
        try{
            const {user} = createUserWithEmailAndPassword(email,password);
            await createUserProfileDocument();
            setDisplayName('');
            setConfirmPassword('');
            setPassword('');
            setEmail('');
        }catch(e){
            console.log(e);
        }

    }

    return(
        <div className="sign-up">
            <h2 className="title">I do not have a account.</h2>
            <span>Sign Up with Your Email and Password.</span>

            <FormInput name="displayName" type="text" value={displayName} 
            label="Display Name" required onchange={(e)=>setDisplayName(e.target.value)} >

            <FormInput name="email" type="email" value={email} 
            label="Email" required onchange={(e)=>setEmail(e.target.value)} ></FormInput>

            <FormInput name="password" type="password" value={password} 
            label="Password" required onchange={(e)=>setPassword(e.target.value)} ></FormInput>

            <FormInput name="confirmPassword" type="password" value={confirmPassword} 
            label="Password" required onchange={(e)=>setConfirmPassword(e.target.value)} ></FormInput>

            </FormInput>
        </div>
    )
}
